function addWatermarkByItemId(a,b){console.log("Запрос на наложение водяного знака. ID элемента: "+a);let btn=document.getElementById("sl3w-add-watermark-btn");BX.ajax({type:"GET",url:"/ajax/sl3w.watermark/add_watermark.php?element_id="+a+"&iblock_id="+b,async:false,dataType:"json",onsuccess:function(c){console.log(c);if(c.watermarked){setBtnColor(btn,"#8fbc8f");btn.textContent="Водяной знак наложен"}else{setBtnColor(btn,"#e9967a");btn.textContent="Ошибка при обработке"}},onfailure:function(){setBtnColor(btn,"#e9967a");btn.textContent="Ошибка при обработке"}});btn.href="javascript:void(0)"}function setBtnColor(b,a){b.style.setProperty("background-color",a,"important");b.style.setProperty("background-image","linear-gradient(bottom, "+a+", #fff)","important");b.style.setProperty("background-image","-mos-linear-gradient(bottom, "+a+", #fff)","important");b.style.setProperty("background-image","-o-linear-gradient(bottom, "+a+", #fff)","important");b.style.setProperty("background-image","-webkit-linear-gradient(bottom, "+a+", #fff)","important");b.style.setProperty("cursor","default","important")};